import React, { useState, useEffect, useRef } from "react";
import socketIOClient from "socket.io-client";
const url = "https://api.coincap.io/v2";
const webUrl = "wss://ws.coincap.io/prices?assets=bitcoin"

function App() {
  const [response, setResponse] = useState("");

  useEffect(() => {
    const socket = socketIOClient(webUrl, { transports: ['websocket', 'polling', 'flashsocket'] });
    socket.on("FromAPI", data => {
      console.log(data);
      setResponse(data);
    });
  }, []);
  console.log(response);
  return (
    <div className="App">
      <h1>{response}</h1>
    </div>
  );
}

export default App;
