import React, { useState, useEffect, useRef } from "react";
function Card(props){
    const ws = useRef(null);
    const [price,setPrice] = useState(0);
    useEffect(() => {
        ws.current = new WebSocket(props.webUrl + props.crypto);
        ws.current.onopen = () => console.log("ws opened");
        ws.current.onclose = () => console.log("ws closed");
        ws.current.onmessage = (e) => {
          const message = JSON.parse(e.data);
          setPrice(message[props.crypto]);
        }
        return () => {
            ws.current.close();
        };
    }, []);
    return(<div className="note">
    <img src="https://assets.coingecko.com/coins/images/279/large/ethereum.png?1595348880" width="50" height="50"/>
    <h1>
    {props.crypto.toUpperCase()}
    </h1>
    <p>{"$ " + price}</p>
  </div>);
}

export default Card;
