import React, { useState, useEffect, useRef } from "react";
import socketIOClient from "socket.io-client";
const url = "https://api.coincap.io/v2";
const webUrl = "wss://ws.coincap.io/prices?assets=bitcoin,ethereum,monero,litecoin"
function App() {
  const ws = useRef(null);

  ws.current = new WebSocket(webUrl);
  const [val, setVal] = useState();
  var dd = [10,20,30,40];
  useEffect(() => {
    ws.current = new WebSocket(webUrl);
    ws.current.onopen = () => console.log("ws opened");
    ws.current.onclose = () => console.log("ws closed");
    ws.current.onmessage = (e) => {
      const message = JSON.parse(e.data);
      setVal((message) => {
        const {litecoin} = message;
        console.log(litecoin);
      })
    }
    return () => {
        ws.current.close();
    };
}, []);
const crypto = ['Bitcoin','Mthereum','Monero','Litecoin'];
  return (
    <div className="App">
      <h1>{val}</h1>
      <ul>
      {crypto.map((item,index)=>{
        return <li>{item} : {val}</li>
      })}
      </ul>
    </div>
  );
}

export default App;
