import React , {useEffect, useState} from "react";
import Card from "../components/Card"
const url = "https://api.coincap.io/v2";
const webUrl = "wss://ws.coincap.io/prices?assets="
import coinGecko from "../apis/coinGeko";

function CoinLandingPage() {
    const [coins,setCoins] = useState([]);

    useEffect(() => {
        useEffect(() => {
            const fetchData = async () => {

              const response = await coinGecko.get("/coins/markets/", {
                params: {
                  vs_currency: "usd",
                  ids: watchList.join(","),
                },
              });
              setCoins(response.data);
              setIsLoading(false);
            };

            if (watchList.length > 0) {
              fetchData();
            } else setCoins([]);
          }, [watchList]);
    },[])
    return(<div>
            <Card webUrl={webUrl} crypto={"ethereum"}/>
            <Card webUrl={webUrl} crypto={"bitcoin"}/>
            <Card webUrl={webUrl} crypto={"litecoin"}/>
    </div>);
}

export default CoinLandingPage;
