const url = "https://api.coincap.io/v2";
const webUrl = "wss://ws.coincap.io/prices?assets="

function App() {
  return (
    <div className="App">
      <h1>Crypto Tracker</h1>
      <ul>
      <Card webUrl={webUrl} crypto={"ethereum"}/>
      <Card webUrl={webUrl} crypto={"bitcoin"}/>
      <Card webUrl={webUrl} crypto={"litecoin"}/>
      </ul>
    </div>
  );
}

export default App;
